// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

import '@openzeppelin/contracts/access/Ownable.sol';

contract FwarBlackList is Ownable{

  mapping(address => bool) public _isBlackList;

  function addToBlackListMultiple(address[] calldata addresses) external onlyOwner {
    for (uint i; i < addresses.length; i++){
      _isBlackList[addresses[i]] = true;
    }
  }

  function removeFromBlackList(address _address) external onlyOwner {
    _isBlackList[_address] = false;
  }

  function isBlackList(address _address) external view returns (bool){
    return _isBlackList[_address];
  }
}
